import React, {useState} from "react"

function Item(){
  return(
    <div className="item-wrapper">
      <div className="question-block"></div>
      <div className="answer-block">
        <div className="team-one"></div>
        <div className="false"></div>
        <div className="team-two"></div>
      </div>
    </div>
  )
}

function ItemRow(){
  const [isActive, setIsActive] = useState(false)

  return (
    <div className="item-row-wrapper">
      <div className="item-row-question-block">Question</div>
      <div className="item-row-question-block">
        {isActive ? <Item/> : <p>100</p>}
      </div>
      <div className="item-row-question-block">
        {isActive ? <Item/> : <span>200</span>}
      </div>
      <div className="item-row-question-block">
        {isActive ? <Item/> : <span>300</span>}
      </div>
      <div className="item-row-question-block">
        {isActive ? <Item/> : <span>400</span>}
      </div>
      <div className="item-row-question-block">
        {isActive ? <Item/> : <span>500</span>}
      </div>
    </div>
  )
}

export {ItemRow}
