import React, {useState} from "react"

function Item(){

  const handleHide = () => {
    document.querySelector('.popup-active').style.display = 'none';
  }

  return(
    <div className=" popup-active ">
      <div className="question-block">This is place for question</div>
      <div className="answer-block">
        <div className="team-one"></div>
        <div className="false"  onClick={handleHide}></div>
        <div className="team-two"></div>
      </div>
    </div>
  )
}

function ItemRow(){
  const [isActive, setIsActive] = useState(false)

  function handleClick(){
    setIsActive(true)
  }

  return (
    <div className="item-row-wrapper">
      <div className="item-row-question-block">Question</div>
      <div className="item-row-question-block" onClick={handleClick}>
        {isActive ? <Item/> : <span>100</span>}
      </div>
      <div className="item-row-question-block" onClick={handleClick}>
        {isActive ? <Item/> : <span>200</span>}
      </div>
      <div className="item-row-question-block" onClick={handleClick}>
        {isActive ? <Item/> : <span>300</span>}
      </div>
      <div className="item-row-question-block" onClick={handleClick}>
        {isActive ? <Item/> : <span>400</span>}
      </div>
      <div className="item-row-question-block" onClick={handleClick}>
        {isActive ? <Item/> : <span>500</span>}
      </div>
    </div>
  )
}

export {ItemRow}
