import React, { useState } from "react";

function Item({ handleHide }) {
  return (
    <div className="popup-active">
      <div className="question-block">This is place for question</div>
      <div className="answer-block">
        <div className="team-one"></div>
        <div className="false" onClick={handleHide}></div>
        <div className="team-two"></div>
      </div>
    </div>
  );
}

function ItemRow() {
  const [isActive, setIsActive] = useState(false);
  const [isHidden, setIsHidden] = useState(false);

  function handleClick() {
    setIsActive(true);
    console.log(isActive)
  }

  function handleHide() {
    setIsHidden(true);
    setIsActive(false);
    console.log(isHidden)
    console.log(isActive)
  }

  return (
    <div className="item-row-wrapper">
      <div className="item-row-question-block">Question</div>
      <div
        className={isHidden ? "item-row-question-block hidden" : "item-row-question-block"}
        onClick={handleClick}
      >
        {isActive ? <Item handleHide={handleHide} /> : <span>100</span>}
      </div>
      <div 
        className={isHidden ? "item-row-question-block hidden" : "item-row-question-block"}
        onClick={handleClick}
      >
        {isActive ? <Item handleHide={handleHide} /> : <span>200</span>}
      </div>
      <div className="item-row-question-block" onClick={handleClick}>
        {isActive ? <Item handleHide={handleHide} /> : <span>300</span>}
      </div>
      <div className="item-row-question-block" onClick={handleClick}>
        {isActive ? <Item handleHide={handleHide} /> : <span>400</span>}
      </div>
      <div className="item-row-question-block" onClick={handleClick}>
        {isActive ? <Item handleHide={handleHide} /> : <span>500</span>}
      </div>
    </div>
  );
}

export { ItemRow };
