import React, {useState} from "react";

function Item({count, question, handleScoreFT, handleScoreST, answer}) {

  const [isActive, setIsActive] = useState(false)
  const [isDisplayed, setIsDisplayed] = useState(true);
  const [wisibleAnswer, setWisibleAnswer] = useState(false)

  function handleActive(){
    setIsActive(true)
  }

  function handleNoneActive(e){
    e.stopPropagation();
    setIsActive(false)
    setIsDisplayed(false)
  }

  const handleAnswerHidden = () => {
    setWisibleAnswer(!onActive)
  }

  return (
      <div className="item-row-question-block block-hover" onClick={handleActive}>
        {!isActive ? <div className="count-info">{isDisplayed ? count: " "}</div> : (      
          <div className="popup-active">
            <div className="question-block">
              {question}
            <button className="answer-btn">
              Ответ
            </button>
            <div className={wisibleAnswer ? " hidden": " "} onClick={handleAnswerHidden}>
              {answer}
            </div>
            </div>
            <div className="answer-block" onClick={handleNoneActive}>
              <div className="team-one" onClick={() => handleScoreFT(count)}>Команда 1</div>
              <div className="false">Нет ответа</div>
              <div className="team-two" onClick={() => handleScoreST(count)}>Команда 2</div>
            </div>
          </div>)
          }
      </div>
  );
}

function ItemRow({data, index, handleScoreFT, handleScoreST}) {
  const info = data[index]

  return (
    <div className="item-row-wrapper">
      <div className="item-row-question-block">{info.name}</div>
      {info.questions.map((item, i) => 
        <Item 
          key={i} 
          count={item.count} 
          question={item.question}
          handleScoreFT={handleScoreFT}
          handleScoreST={handleScoreST}
          answer={item.answer}
        />)}
    </div>
  );
}

export { ItemRow };
