import React, {useState} from "react";

function Item({count}) {
  const [isActive, setIsActive] = useState(false)
  const [isDisplayed, setIsDisplayed] = useState(true);

  function handleActive(){
    setIsActive(true)
  }
  console.log(isActive)

  function handleNoneActive(e){
    e.stopPropagation();
    setIsActive(false)
    setIsDisplayed(false)
  }
  console.log(isActive)

  return (
      <div className="item-row-question-block" onClick={handleActive}>
        {!isActive ? <span>{isDisplayed ? {count}: " "}</span> : (      
          <div className="popup-active">
            <div className="question-block">This is place for question {count}</div>
            <div className="answer-block">
              <div className="team-one"></div>
              <div className="false" onClick={handleNoneActive}></div>
              <div className="team-two"></div>
            </div>
          </div>)
          }
      </div>
  );
}

function ItemRow() {
  return (
    <div className="item-row-wrapper">
      <div className="item-row-question-block">Question</div>
      <Item count={100}/>
      <Item count={200}/>
      <Item count={300}/>
      <Item count={400}/>
      <Item count={500}/>
    </div>
  );
}

export { ItemRow };
