import React, { useState } from 'react';

function Team() {
  const [teamName, setTeamName] = useState('');
  const [savedTeamName, setSavedTeamName] = useState('');
  const [onActive, setOnActive] = useState(false)
  const [score, setScore] = useState(0)

  const handleInputChange = (event) => {
    setTeamName(event.target.value);
  };

  const handleSaveClick = () => {
    setSavedTeamName(teamName);
  };

  const handleFormHidden = () => {
    setOnActive(!onActive)
  }

  return (
    <div className="team-wrapper">
      <h2>{savedTeamName}</h2>
      <form>
        <label htmlFor="teamName" onClick={handleFormHidden}>Team Name:</label>
        <input type="text" id="teamName" value={teamName} onChange={handleInputChange} className={onActive ? "hidden": ''}/>
        <button type="button" onClick={handleSaveClick} className={onActive ? "hidden": ''}>Save</button>
      </form>
      <div className='team-score'>

      </div>
      <div className='total-account'>

      </div>
    </div>
  );
}

export { Team };
